(48, 0, 0, 'monthDe', '', 0, 0, 0, '$month_arr = array(''01'' => ''Januar'',\n                   ''02'' => ''Februar'',\n                   ''03'' => ''März'',\n                   ''04'' => ''April'',\n                   ''05'' => ''Mai'',\n                   ''06'' => ''Juni'',\n                   ''07'' => ''Juli'',\n                   ''08'' => ''August'',\n                   ''09'' => ''September'',\n                   ''10'' => ''Oktober'',\n                   ''11'' => ''November'',\n                   ''12'' => ''Dezember''\n                  );\n$month = strftime(''%m'', $time);\n    \nreturn "$month_arr[$month]";', 0, 'a:0:{}', '', 0, ''),
(49, 1, 0, 'AjaxForm', '', 0, 17, 0, '/** @var array $scriptProperties */\n/** @var AjaxForm $AjaxForm */\nif (!$modx->loadClass(''ajaxform'', MODX_CORE_PATH . ''components/ajaxform/model/ajaxform/'', false, true)) {\n    return false;\n}\n$AjaxForm = new AjaxForm($modx, $scriptProperties);\n\n$snippet = $modx->getOption(''snippet'', $scriptProperties, ''FormIt'', true);\n$tpl = $modx->getOption(''form'', $scriptProperties, ''tpl.AjaxForm.example'', true);\n$formSelector = $modx->getOption(''formSelector'', $scriptProperties, ''ajax_form'', true);\n$objectName = $modx->getOption(''objectName'', $scriptProperties, ''AjaxForm'', true);\n$AjaxForm->loadJsCss($objectName);\n\n/** @var pdoTools $pdo */\nif (class_exists(''pdoTools'') && $pdo = $modx->getService(''pdoTools'')) {\n    $content = $pdo->getChunk($tpl, $scriptProperties);\n} else {\n    $content = $modx->getChunk($tpl, $scriptProperties);\n}\nif (empty($content)) {\n    return $modx->lexicon(''af_err_chunk_nf'', array(''name'' => $tpl));\n}\n\n// Add selector to tag form\nif (preg_match(''#<form.*?class=(?:"|\\'')(.*?)(?:"|\\'')#i'', $content, $matches)) {\n    $classes = explode('' '', $matches[1]);\n    if (!in_array($formSelector, $classes)) {\n        $classes[] = $formSelector;\n        $classes = preg_replace(\n            ''#class=(?:"|\\'')'' . $matches[1] . ''(?:"|\\'')#i'',\n            ''class="'' . implode('' '', $classes) . ''"'',\n            $matches[0]\n        );\n        $content = str_ireplace($matches[0], $classes, $content);\n    }\n} else {\n    $content = str_ireplace(''<form'', ''<form class="'' . $formSelector . ''"'', $content);\n}\n\n// Add method = post\nif (preg_match(''#<form.*?method=(?:"|\\'')(.*?)(?:"|\\'')#i'', $content)) {\n    $content = preg_replace(''#<form(.*?)method=(?:"|\\'')(.*?)(?:"|\\'')#i'', ''<form\\\\1method="post"'', $content);\n} else {\n    $content = str_ireplace(''<form'', ''<form method="post"'', $content);\n}\n\n// Add action for form processing\n$hash = md5(http_build_query($scriptProperties));\n$action = ''<input type="hidden" name="af_action" value="'' . $hash . ''" />'';\nif ((stripos($content, ''</form>'') !== false)) {\n    if (preg_match(''#<input.*?name=(?:"|\\'')af_action(?:"|\\'').*?>#i'', $content, $matches)) {\n        $content = str_ireplace($matches[0], '''', $content);\n    }\n    $content = str_ireplace(''</form>'', "\\n\\t$action\\n</form>", $content);\n}\n\n// Save settings to user`s session\n$_SESSION[''AjaxForm''][$hash] = $scriptProperties;\n\n// Call snippet for preparation of form\n$action = !empty($_REQUEST[''af_action''])\n    ? $_REQUEST[''af_action'']\n    : $hash;\n\n$AjaxForm->process($action, $_REQUEST);\n\n// Return chunk\nreturn $content;', 0, 'a:7:{s:4:"form";a:7:{s:4:"name";s:4:"form";s:4:"desc";s:18:"ajaxform_prop_form";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:20:"tpl.AjaxForm.example";s:7:"lexicon";s:19:"ajaxform:properties";s:4:"area";s:0:"";}s:7:"snippet";a:7:{s:4:"name";s:7:"snippet";s:4:"desc";s:21:"ajaxform_prop_snippet";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:6:"FormIt";s:7:"lexicon";s:19:"ajaxform:properties";s:4:"area";s:0:"";}s:12:"frontend_css";a:7:{s:4:"name";s:12:"frontend_css";s:4:"desc";s:26:"ajaxform_prop_frontend_css";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:29:"[[+assetsUrl]]css/default.css";s:7:"lexicon";s:19:"ajaxform:properties";s:4:"area";s:0:"";}s:11:"frontend_js";a:7:{s:4:"name";s:11:"frontend_js";s:4:"desc";s:25:"ajaxform_prop_frontend_js";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:27:"[[+assetsUrl]]js/default.js";s:7:"lexicon";s:19:"ajaxform:properties";s:4:"area";s:0:"";}s:9:"actionUrl";a:7:{s:4:"name";s:9:"actionUrl";s:4:"desc";s:23:"ajaxform_prop_actionUrl";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:24:"[[+assetsUrl]]action.php";s:7:"lexicon";s:19:"ajaxform:properties";s:4:"area";s:0:"";}s:12:"formSelector";a:7:{s:4:"name";s:12:"formSelector";s:4:"desc";s:26:"ajaxform_prop_formSelector";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:9:"ajax_form";s:7:"lexicon";s:19:"ajaxform:properties";s:4:"area";s:0:"";}s:10:"objectName";a:7:{s:4:"name";s:10:"objectName";s:4:"desc";s:24:"ajaxform_prop_objectName";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:8:"AjaxForm";s:7:"lexicon";s:19:"ajaxform:properties";s:4:"area";s:0:"";}}', '', 0, 'core/components/ajaxform/elements/snippets/snippet.ajaxform.php');
