(1, 0, 0, 'Главная', 'indexTemplate', 0, 0, '', 0, '<!doctype html>\n<html lang="[[++cultureKey]]">\n[[$base.head]]\n<body>\n    [[$header]]\n    [[$base.nav]]\n    [[$slider]]\n    [[$index.content]]\n    [[$carusel.product]]\n    [[$news.index]]\n    [[$footer]]\n</body>\n</html>\n', 0, 'a:0:{}', 0, ''),
(2, 0, 0, 'Текст', 'TextTemplate', 0, 0, '', 0, '<!doctype html>\n<html lang="[[++cultureKey]]">\n[[$base.head]]\n<body>\n    [[$header]]\n    [[$base.nav]]\n    [[$title.block]]\n    [[$text.content]]\n    [[$footer]]\n</body>\n</html>\n', 0, 'a:0:{}', 0, ''),
(3, 0, 0, 'Новость', 'NewsTemplate', 0, 0, '', 0, '<!doctype html>\n<html lang="[[++cultureKey]]">\n[[$base.head]]\n<body>\n    [[$header]]\n    [[$base.nav]]\n    [[$title.block]]\n    <div class="innerTextHolder">\n        <div class="container">\n            <div class="newsTextHolder">\n                  <p class="introtext">[[*introtext:empty=`[[*newsIntrotext]]`]]</p>\n[[*imagePaste:eq=`1`:then=`[[$image.content]]`]]                  \n                  [[*content]]\n              </div>\n        </div>\n          [[$[[*id:eq=`2`:then=`mapHolder`]]]]\n      </div>\n    [[$footer]]\n</body>\n</html>\n', 0, 'a:0:{}', 0, ''),
(4, 0, 0, 'Товар', 'ProductTemplate', 0, 0, '', 0, '<!doctype html>\n<html lang="[[++cultureKey]]">\n[[$base.head]]\n<body>\n    [[$header]]\n    [[$base.nav]]\n    [[$title.block]]\n    <div class="innerTextHolder">\n        <div class="container">\n            <div class="productContainer">\n                <div class="productImageContainer">\n                    <picture>\n                          <a rel="colorbox" href="[[*product-image]]"><source type="image/jpg"\n                            srcset="[[*product-image]] 1x, [[*retina-product-image:empty=`[[*product-image]]`]] 2x"></a>\n                          <a rel="colorbox" href="[[*product-image]]"><img src="[[*product-image]]" alt="[[*pagetitle]]"></a>\n                    </picture>\n                </div>\n                  <p class="introtext">\n<span class="introtextBg"></span>\n[[*introtext:empty=`[[*newsIntrotext]]`]]</p>\n                  <h3 class="price">от [[*price]] BYN</h3>\n                  <div class="productDescription">\n                      [[*content]]\n                  </div>\n                  <div class="productTech">\n                      [[!getImageList? &tpl=`tpl.tech-product`  \n                &limit=`50` &tvname=`product-tech`]]\n                  </div>\n             </div>\n        </div>\n      </div>\n    [[$footer]]\n</body>\n</html>\n', 0, 'a:0:{}', 0, ''),
(5, 0, 0, 'Категория', 'CategoryTemplate', 0, 0, '', 0, '<!doctype html>\n<html lang="[[++cultureKey]]">\n[[$base.head]]\n<body>\n    [[$header]]\n    [[$base.nav]]\n    [[$title.block]]\n    <div class="innerTextHolder">\n        <div class="container">\n          [[!pdoPage? &depth=`0` &tpl=`tpl.product` &limit=`6` &includeContent=`1` &includeContent=`1` &showHidden=`1` \n&includeTVs=`product-image,retina-product-image,price` &prepareTVs=`1` &pageLimit=`3` &cache=`1` &cacheTime=`1800` \n&maxLimit=`100` &sortby=`[[+publishedon]]` &sortdir=`DESC` \n&tplPage=`@INLINE <li><a href="[[+href]]">[[+pageNo:lte=`9`:then=`0[[+pageNo]]`:else=`[[+pageNo]]`]]</a></li>`\n&tplPageWrapper=`@INLINE <div class="pagination"><ul class="pagination">[[+pages]]</ul></div>`\n&tplPageFirst=`@INLINE <li class="control"><a href="[[+href]]">0[[%pdopage_first]]</a></li>`\n&tplPageActive=`@INLINE <li class="active"><a href="[[+href]]">[[+pageNo:lte=`9`:then=`0[[+pageNo]]`:else=`[[+pageNo]]`]]</a></li>`	\n]]\n    [[!+page.nav]]  \n        </div>\n        \n     </div>\n    [[$footer]]\n</body>\n</html>\n', 0, 'a:0:{}', 0, ''),
